mapper.select:
select count(article_no)
from board
where isnotice = #{isnotice}

mapper.update:
update board
set hit = hit + 1
where article_no = #{articleNo}

update board
set recommendation = recommendation + 1
where article_no = #{articleNo}

update board
set subject = #{subject}, content = #{content}
where article_no = #{articleNo}
